mapper.select:
SELECT COUNT(*) FROM tb_memo a, tb_yuser u WHERE a.YID = u.YID
AND a.TITLE LIKE CONCAT('%', #{keyword}, '%')
AND u.YNAME LIKE CONCAT('%', #{keyword}, '%')
AND date_format(a.UPDDT,'%Y-%m-%d') >= #{startDate}
AND date_format(a.UPDDT,'%Y-%m-%d') <= #{endDate}

SELECT a.SEQ, a.TITLE, a.YID, u.YNAME, a.REGDT, a.UPDDT
FROM tb_memo a, tb_yuser u
WHERE a.YID = u.YID




                AND a.TITLE LIKE CONCAT('%', #{keyword}, '%')


                AND u.YNAME LIKE CONCAT('%', #{keyword}, '%')






        AND date_format(a.UPDDT,'%Y-%m-%d') >= #{startDate}




        AND date_format(a.UPDDT,'%Y-%m-%d') <= #{endDate}

ORDER BY a.SEQ DESC
LIMIT #{size} OFFSET #{start}
